import java.util.Arrays;import java.util.Scanner;public class ThemSuaXoa {    public static void main(String[] args) {        int[] arr = {2, 4, 6, 5, 3, 7, 8, 9, 1};        int[] arr2 = new int[arr.length + 1];        Scanner Add = new Scanner(System.in);        System.out.println("nhập vị trí muốn thêm từ 0 tới " + (arr2.length-1) + " :");        int n = Add.nextInt();        for (int i = 0, j = 0; i < arr2.length; i++) {            if (i == n) {                Scanner a = new Scanner(System.in);                System.out.println("nhập số muốn thêm :");                int so = a.nextInt();                arr2[j] = so;            } else {                arr2[i] = arr[j++];            }        }        arr = arr2;        System.out.println("Mảng sau khi thêm là: ");        System.out.println(Arrays.toString(arr));    }}class Edit {    public static void main(String[] args) {        int[] arr = {2, 4, 6, 5, 3, 7, 8, 9, 1};        int[] arr2 = new int[arr.length ];        Scanner Edit = new Scanner(System.in);        System.out.println("nhập vị trí muốn sửa từ 0 tới " + (arr2.length-1) + " :");        int n = Edit.nextInt();        for (int i = 0; i < arr2.length; i++) {            if (i == n) {                Scanner a = new Scanner(System.in);                System.out.println("nhập số muốn sửa :");                int so = a.nextInt();                arr2[i] = so;            } else if (i < arr.length) {                arr2[i] = arr[i];            }        }        System.out.println("Mảng sau khi sửa là: ");        System.out.println(Arrays.toString(arr2));    }}class Xoa {    public static void main(String[] args) {        int[] arr = {2, 4, 6, 5, 3, 7, 8, 9, 1};        int[] arr2 = new int[arr.length ];        Scanner Edit = new Scanner(System.in);        System.out.println("nhập vị trí muốn xoá từ 0 tới " + (arr.length-1) + " :");        int n = Edit.nextInt();        if (n < 0 || n >= arr.length) {            System.out.println("Vị trí không hợp lệ. Vui lòng nhập lại !");        } else {            for (int i = 0, j = 0; i < arr.length; i++) {                if (i != n) {                    arr2[j++] = arr[i];                }            }        }        arr2 = Arrays.copyOf(arr2, arr2.length - 1);        System.out.println("Mảng sau khi xoá là:");        System.out.println(Arrays.toString(arr2));    }}class TimKiem{    public static void main(String[] args) {        int[] arr = {2, 4, 6, 5, 3, 7, 8, 9, 1};        Scanner Edit = new Scanner(System.in);        System.out.println("nhập số ở vị trí muốn tìm kiếm từ 0 tới " + (arr.length-1) + " :");        int n = Edit.nextInt();        for(int i=0;i<arr.length;i++){            if(i==n){                System.out.printf("số ở vị trí "+n+" là ; " + arr[i]);            }        }    }}